import React,{useState} from 'react'
import './css/houseproducts.css'

function HouseStateUpdate({house}) {
    const { id } = house;
    const [markAsRead,setMarkedAsRead]=useState("")

      function handleMarkedAsRead(id) {
      setMarkedAsRead((markAsRead) => !markAsRead);
    }
     const MarkedAsRead= markAsRead ? "Mark as unread" : "Mark as read"


  return (
    <div>
        <button  onClick={handleMarkedAsRead} className='imageButton2 imageButton3'>{MarkedAsRead}</button>
      </div>
  )
}

export default HouseStateUpdate
